<?php

namespace App\Repository;

use App\Entity\Bateau;
use Doctrine\Bundle\DoctrineBundle\Repository\ServiceEntityRepository;
use Doctrine\Common\Persistence\ManagerRegistry;

/**
 * BateauRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class BateauRepository extends ServiceEntityRepository
{
    public function __construct(ManagerRegistry $registry)
    {
        parent::__construct($registry, Bateau::class);
    }

    public function getNbRameursQueryBuilder($nbRameurs)
    {
        if ($nbRameurs != 0) {
            return $this
                ->createQueryBuilder('b')
                ->join('b.type', 'type')
                ->where('type.nbplacerameurs = :nbRameurs')
                ->andWhere('b.datesupp is null')
                ->orderBy('b.nom')
                ->setParameter('nbRameurs', $nbRameurs);
        } else {
            return $this
                ->createQueryBuilder('b')
                ->where('b.datesupp is null')
                ->orderBy('b.nom');
        }
    }

    public function getListeBateaux()
    {
        $qb = $this
            ->createQueryBuilder('b')
            ->join('b.type', 'type')
            ->where('b.datesupp is null')
            ->orderBy('type.nom')
            ->addOrderBy('b.nom');

        return $qb
            ->getQuery()
            ->getResult();
    }
}
